import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Button, Form, Input, Upload, Table } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import { DragDropContext, Draggable, Droppable } from 'react-beautiful-dnd';
import { updateMediaOrder } from './ducks/slice';
import { mediaList as mediaThunk } from './ducks/thunks';
import axiosInstance from '../../config/axiosConfig';
import { columns } from './utils/data';

const MediaPage = () => {
  const dispatch = useDispatch();
  const { mediaList, loading, error } = useSelector((state) => state.media);

  // Fetch media list on component mount
  useEffect(() => {
    dispatch(mediaThunk());
  }, [dispatch]);

  // Handle file upload
  const handleUpload = async (values) => {
    const formData = new FormData();
    formData.append('file', values.file.file.originFileObj);
    formData.append('tags', values.tags);

    try {
      await axiosInstance.post('/files/upload', formData);
      dispatch(mediaThunk()); // Refresh the media list
    } catch (error) {
      console.error('Error uploading media:', error);
    }
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const reorderedData = Array.from(data);
    const [movedItem] = reorderedData.splice(result.source.index, 1);
    reorderedData.splice(result.destination.index, 0, movedItem);

    setData(reorderedData);
  };
  const DraggableBodyRow = ({ index, className, style, ...restProps }) => (
    <Draggable draggableId={mediaList[index]?.id?.toString()} index={index}>
      {(provided) => (
        <tr
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          className={className}
          style={{
            ...style,
            ...provided.draggableProps.style,
          }}
        >
          {restProps.children}
        </tr>
      )}
    </Draggable>
  );
  return (
    <div style={{ maxWidth: 800, margin: '0 auto', padding: '20px' }}>
      <h2>Media Management</h2>

      {/* Upload Form */}
      <Form layout="vertical" onFinish={handleUpload}>
        <Form.Item
          name="file"
          label="Upload Media"
          valuePropName="file"
          rules={[{ required: true, message: 'Please upload a media file' }]}
        >
          <Upload beforeUpload={() => false} maxCount={1}>
            <Button icon={<UploadOutlined />}>Select File</Button>
          </Upload>
        </Form.Item>
        <Form.Item name="tags" label="Tags" rules={[{ required: true, message: 'Please add tags' }]}>
          <Input placeholder="Enter comma-separated tags" />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Upload
          </Button>
        </Form.Item>
      </Form>

      {/* Error Display */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Media List */}
      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="table" direction="vertical">
          {(provided) => (
            <Table
            columns={columns}
            dataSource={mediaList?.map((d) => ({key: d.id, ...d}))}
            components={{
              body: {
                wrapper: (props) => (
                  <tbody {...provided.droppableProps} ref={provided.innerRef}>
                    {props.children}
                    {provided.placeholder}
                  </tbody>
                ),
                row: DraggableBodyRow,
              },
            }}
            pagination={false} // Disable pagination for drag-and-drop
          />
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default MediaPage;
