import React, { useEffect, useState } from 'react';
import { Modal, Spin } from 'antd';
import axiosInstance from '../../config/axiosConfig';

const MediaViewModal = ({ visible, onClose, mediaId }) => {
  const [loading, setLoading] = useState(false);
  const [media, setMedia] = useState(null);

  // Fetch media details and update view count
  const fetchMediaDetails = async () => {
    setLoading(true);
    try {
      // Fetch media details
      const response = await axiosInstance.get(`/files/${mediaId}`);
      setMedia(response.data?.file);

      // Update view count
      await axiosInstance.post(`/files/${mediaId}/increment-view`);
    } catch (error) {
      console.error('Error fetching media details:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (visible && mediaId) {
      fetchMediaDetails();
    }
  }, [visible, mediaId]);

  return (
    <Modal
      visible={visible}
      title="Media Details"
      onCancel={onClose}
      footer={null}
      width={800}
    >
      {loading ? (
        <div style={{ textAlign: 'center', padding: '20px' }}>
          <Spin size="large" />
        </div>
      ) : (
        media && (
          <div>
            <h2>{media.filename}</h2>
            <p><strong>File Type:</strong> {media.filetype}</p>
