import { configureStore } from '@reduxjs/toolkit';
import authReducer from '../modules/auth/authSlice';
import mediaReducer from '../modules/media/mediaSlice';

const store = configureStore({
  reducer: {
    auth: authReducer,
    media: mediaReducer,
  },
});

export default store;
