import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Button, Form, Input, Upload, List, Tag } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { fetchMedia, updateMediaOrder } from './ducks/slice';
import axiosInstance from '../../config/axiosConfig';

const MediaPage = () => {
  const dispatch = useDispatch();
  const { mediaList, loading, error } = useSelector((state) => state.media);

  // Fetch media list on component mount
  useEffect(() => {
    dispatch(fetchMedia());
  }, [dispatch]);

  // Handle file upload
  const handleUpload = async (values) => {
    const formData = new FormData();
    formData.append('file', values.file.file.originFileObj);
    formData.append('tags', values.tags);

    try {
      await axiosInstance.post('/files/upload', formData);
      dispatch(fetchMedia()); // Refresh the media list
    } catch (error) {
      console.error('Error uploading media:', error);
    }
  };

  // Handle drag-and-drop reordering (frontend-only)
  const handleDragEnd = (result) => {
    if (!result.destination) return; // Ignore if dropped outside

    const reorderedItems = Array.from(mediaList);
    const [movedItem] = reorderedItems.splice(result.source.index, 1);
    reorderedItems.splice(result.destination.index, 0, movedItem);

    // Update the media list in Redux (frontend only)
    dispatch(updateMediaOrder(reorderedItems));
  };

  return (
    <div style={{ maxWidth: 800, margin: '0 auto', padding: '20px' }}>
      <h2>Media Management</h2>

      {/* Upload Form */}
      <Form layout="vertical" onFinish={handleUpload}>
        <Form.Item
          name="file"
          label="Upload Media"
          valuePropName="file"
          rules={[{ required: true, message: 'Please upload a media file' }]}
        >
          <Upload beforeUpload={() => false} maxCount={1}>
            <Button icon={<UploadOutlined />}>Select File</Button>
          </Upload>
        </Form.Item>
        <Form.Item name="tags" label="Tags" rules={[{ required: true, message: 'Please add tags' }]}>
          <Input placeholder="Enter comma-separated tags" />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Upload
          </Button>
        </Form.Item>
      </Form>

      {/* Error Display */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Media List */}
      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="media-list">
          {(provided) => (
            <List
              {...provided.droppableProps}
              ref={provided.innerRef}
              loading={loading}
              dataSource={mediaList}
              renderItem={(item, index) => (
                <Draggable key={item.id} draggableId={item.id.toString()} index={index}>
                  {(provided) => (
                    <List.Item
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                    >
                      <div style={{ display: 'flex', flexDirection: 'column' }}>
                        <strong>{item.filename}</strong>
                        <div>
                          {item.tags.split(',').map((tag, i) => (
                            <Tag key={i}>{tag.trim()}</Tag>
                          ))}
                        </div>
                      </div>
                    </List.Item>
                  )}
                </Draggable>
              )}
            >
              {provided.placeholder}
            </List>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default MediaPage;
